import React from 'react';
import { Card, CardImg, CardBody, CardTitle, CardText } from 'reactstrap';

class DishDetail extends React.Component {   
    constructor(props) {
        super(props);
        this.state = {
            selectedDish: this.props.selectedDish
        }
    }

    renderComments(comments) {
        if (comments != null) {  
            //console.log(comments);          
            const comments = this.state.selectedDish.comments.map((comment) => {
                comment.date = comment.date.substring(0, comment.date.indexOf('T'));
                return (
                    <li key={comment.id} >
                        <p>{ comment.comment }</p> 
                        <p>{ comment.author } , { comment.date }</p> 
                    </li>
                );
            });
            return(
                {comments}
            )

        }
        else {
            return (
                <div></div>
            );
        }
    }

    render() {
        // const comments = this.state.selectedDish.comments.map((comment) => {
        //     comment.date = comment.date.substring(0, comment.date.indexOf('T'));
        //     return (
        //         <li key={comment.id} >
        //             <p>{ comment.comment }</p> 
        //             <p>{ comment.author } , { comment.date }</p> 
        //         </li>
        //     );
        // });

        return (
            <div className="row">
                <div className="col-md-5 m-1">
                    <Card>
                        <CardImg width='100%' src={this.state.selectedDish.image} alt={this.state.selectedDish.name} />
                        <CardBody>
                            <CardTitle>{this.state.selectedDish.name}</CardTitle>
                            <CardText>{this.state.selectedDish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>
                <div className="col-md-5 m-1">
                    <h4>Comments</h4>      
                    <ul className="list-unstyled">                                       
                        { this.renderComments(this.state.selectedDish.comments) }
                    </ul>
                </div>
            </div>
        )
    }
}

export default DishDetail;
