import React, {Component} from "react";
import {Card , CardImg, CardImgOverlay, CardText,CardBody, CardTitle,BreadcrumbItem,Breadcrumb, Media, Button, Form, FormGroup,Input, Col, Modal, ModalHeader, ModalBody, Row, Label} from 'reactstrap';
import {Link} from 'react-router-dom';
import { Control, LocalForm, Errors } from 'react-redux-form';

    
    
    function RenderDish({dish}){
        if(dish != null){
            return(
                    <div className="col-12 col-md-5 m-1">
                    <Card>
                        <CardImg width="100%" src={dish.image} alt={dish.name}/>
                        <CardBody>
                            <CardTitle> {dish.name}</CardTitle>
                            <CardText>{dish.description}</CardText>
                        </CardBody>
                    </Card>
                    </div>
            )
        }
        else{
            return(
                <div></div>
            )
        }
    }
    

    function RenderComments({comments}){
        if(comments != null){
            const commentss = comments.map((com) => {
                return (
                  <div key={com.id}>
                    <Media tag="li">
                      <Media body >
                        <p>{com.comment}</p>
                        <p>{com. author} ,{new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(com.date)))}</p>
                      </Media>
                    </Media>
                  </div>
                );
            })
            return(
                    <div className="col-12 col-md-5 m-1">
                        <h4> Comments</h4>
                        <Media list className="list-unstyled">
                        {commentss}
                        </Media>
                        <CommentForm />
                        
                    </div>
            )
        }
        else{
            return(
                <div>
                </div>
            )
        }
    }


    const maxLength = (len) => (val) => !(val) || (val.length <= len);
    const minLength = (len) => (val) => (val) && (val.length >= len);

    class CommentForm extends Component{
        constructor(props){
            super(props);
            this.state={
                isModalOpen : false
            };
            this.toggleModal = this.toggleModal.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
        }

        toggleModal(){
            this.setState({
                isModalOpen: !this.state.isModalOpen
            });
        }

        handleSubmit(values){
            console.log("Current state is: " + JSON.stringify(values));
            alert("Current state is: " + JSON.stringify(values));
        }

        
        render(){
            return (
                <div> 
                <Button outline onClick={this.toggleModal}>
                    <span className="fa fa-pencil fa-lg"></span>
                     Submit Comment
                </Button>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
                    <ModalBody>
                    <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
                    <Row className= "form-group">
                                <Label htmlFor="rating" md={12}>Rating</Label>
                            <Col md={12}>
                                <Control.select model=".rating" name="rating"
                                className="form-control">
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </Control.select>
                            </Col>
                        </Row>
                        <Row className= "form-group">
                        <Label htmlFor="author" md={12}>   Your Name</Label>
                        <Col md={12}>
                            <Control.text model=".author" id="author" name="author"
                            placeholder="Your Name" 
                            className="form-control"
                            validators={{
                                 minLength: minLength(3), maxLength: maxLength(15)
                            }}
                            />
                            <Errors 
                            className="text-danger"
                            model=".author"
                            show="touched"
                            messages={{
                                minLength: 'Must be greater than 2 characters',
                                maxLength: 'Must be 15 characters or less'
                            }}
                            /> 
                        </Col>
                        </Row>
                        <Row className= "form-group">
                        <Label htmlFor="comment" md={12}>Comment</Label>
                        <Col md={12}>
                            <Control.textarea model=".comment" id="comment" name="comment"
                            rows="10"
                            className="form-control" />
                        </Col>
                        </Row>
                        <Row className= "form-group">
                            <Col md={10}>
                                <Button type="submit" color="primary">
                                    Submit
                                </Button>
                            </Col>
                        </Row>
                        
                        
                    </LocalForm>
                    </ModalBody>

                </Modal>
                </div>
            )
        }

    }

    class DishDetail extends Component {
        constructor(props){
            super(props);
        }

        render(){
            if(this.props.dish !=null)
        return(   
            <div className="container">
                <div className="row">
                        <Breadcrumb>
                            <BreadcrumbItem>
                                <Link to='/menu'> Menu </Link>
                            </BreadcrumbItem>
                            <BreadcrumbItem active>
                                {this.props.dish.name}
                            </BreadcrumbItem> 
                        </Breadcrumb>
                        <div className="col-12">
                            <h3>{this.props.dish.name}</h3>
                            <hr />
                        </div>
                </div>
                <div className="row">
                        <RenderDish dish={this.props.dish}/>
                        <RenderComments comments = {this.props.comments}/>
                </div>
            </div>
        );
        else 
            return (
                <div></div>
            );
        }

        
    }



export default DishDetail;
