import React from 'react';
import { Card, CardImg, CardBody, CardTitle, CardText, ListGroup } from 'reactstrap';

class DishDetail extends React.Component {    
    render() {
        console.log(this.props.selectedDish.comments);

        const comments = this.props.selectedDish.comments.map((comment) => {
            return (
                <li>
                    <div>{ comment.id }</div> 
                    <div>{ comment.rating }</div> 
                    <div>{ comment.comment }</div> 
                    <div>{ comment.author }</div> 
                    <div>{ comment.date }</div> 
                </li>
            );
        });

        return (
            <div className="row">
                <div className="col-md-5 m-1">
                    <Card>
                        <CardImg width='100%' src={this.props.selectedDish.image} alt={this.props.selectedDish.name} />
                        <CardBody>
                            <CardTitle>{this.props.selectedDish.name}</CardTitle>
                            <CardText>{this.props.selectedDish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>
                <div className="col-md-5 m-1">
                    <ul>
                        {comments}
                    </ul>
                </div>
            </div>
        )
    }
}

export default DishDetail;
