import React, { useState, Component } from 'react';
import { Row, Button, Label, Col, Modal, ModalHeader, ModalBody } from 'reactstrap';

class CommentForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showModal: false
        }
        this.toggleModal = this.toggleModal.bind(this);
    }

    toggleModal() {
        this.setState({
            showModal: !this.state.showModal
        })
    }

    render() {
        return (
            <div>
                <Button outline color="outline-dark" onClick={this.toggleModal}><i className="fa fa-pencil"></i> Submit Comment</Button>
                <Modal isOpen={this.state.showModal} toggle={this.toggleModal} className="modal">
                    <ModalHeader>
                            Submit Comment
                    </ModalHeader>
                    <ModalBody>
                        Modal body
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}


export default CommentForm;
