import React from 'react';
import { Card, CardImg, CardBody, CardTitle, CardText } from 'reactstrap';

class DishDetail extends React.Component {   
    renderComments(dishComment) {
        if (dishComment != null) {  
            const comments = dishComment.map((cmnt) => {    
                let date = new Date(cmnt.date);
                date = date.toString('MM-YYYY');

                return (                                
                    <li key = { cmnt.id } >
                        <p>{ cmnt.comment }</p> 
                        <p>-- { cmnt.author } , { date }</p> 
                    </li>
                )
            });

            return (  
                <div>
                    <h4>Comments</h4>                              
                    <ul className="list-unstyled"> 
                        { comments }
                    </ul> 
                </div> 
            )
        }
        else {
            return (
                <div>No Comments</div>
            );
        }
    }

    render() {       
        return (
            <div className="row">
                <div className="col-md-5 mt-1">
                    <Card>
                        <CardImg width='100%' src={this.props.selectedDish.image} alt={this.props.selectedDish.name} />
                        <CardBody>
                            <CardTitle>{this.props.selectedDish.name}</CardTitle>
                            <CardText>{this.props.selectedDish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>

                <div className="col-md-5 mt-1">                                                             
                    { this.renderComments(this.props.selectedDish.comments) }                  
                </div>
            </div>
        )
    }
}

export default DishDetail;
