import React, {Component} from 'react';
import {Card, CardImg, CardText, CardBody, CardTitle, BreadcrumbItem, Breadcrumb, Button, Modal, ModalBody, ModalHeader, Label, Row, Col} from 'reactstrap';
import {Link} from 'react-router-dom';
import {Control, LocalForm, Errors} from 'react-redux-form';    


const required = val => val && val.length;
const maxLength = len => val => !(val) || val.length <= len;
const minLength = len => val => (val) && val.length >= len;

class CommentForm extends Component {
    constructor(props) {
        super(props)
        this.state = {
            modalFlag: false
        }
        this.toggleModal = this.toggleModal.bind(this); 
    }

    toggleModal() {
        this.setState({
            modalFlag: !this.state.modalFlag
        })
    }

    handleSubmit(values) {
        this.toggleModal();
        alert(JSON.stringify(values));
        console.log(JSON.stringify(values));
    }

    render(){
        return (
            <React.Fragment>
                <Button onClick={this.toggleModal} outline color="secondary"><span className="fa fa-pencil"></span> Submit Comment</Button>
                
                <Modal isOpen={this.state.modalFlag} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Submit Comments</ModalHeader>

                    <ModalBody>
                        <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
                            <Row className="form-group">
                                <Col md={{size: 12}}>
                                        <Label>Rating</Label>
                                </Col>
                                <Col md={{size:12}}>
                                    <Control.select model=".rating" name="rating"
                                        className="form-control">
                                        <option>1</option>
                                        <option>2</option>
                                        <option>3</option>
                                        <option>4</option>
                                        <option>5</option>
                                    </Control.select>
                                </Col>
                            </Row>

                            <Row className="form-group">
                                <Label htmlFor="author" xs={12}>Your Name</Label>
                                <Col xs={12}>
                                    <Control.text model=".author" id="author" name="author"
                                        placeholder="Your Name" className="form-control"
                                        validators={{
                                            required, minLength: minLength(3), maxLength: maxLength(15)
                                        }}
                                    />
                                    <Errors className="text-danger" model=".author" show="touched"
                                        messages={{
                                            required: "Required.",
                                            minLength: " Length must be greater than 3 characters",
                                            maxLength: " Length must be 15 characters or less"
                                        }}
                                    />
                                </Col>
                            </Row>

                            <Row className="form-group">
                                <Label htmlFor="comment" xs={12}>Comments</Label>
                                <Col xs={12}>
                                    <Control.textarea model=".comment" id="comment" name="comment"
                                        rows="6" className="form-control" 
                                    />
                                </Col>
                            </Row>
                            <Button color="primary">Submit</Button>
                        </LocalForm>
                    </ModalBody>
                </Modal>
            </React.Fragment>
        )
    }
}


const RenderDish = ({dish}) => {
    if(dish != null) {
        return (    
            <Card>
                <CardImg width="100%" src={dish.image} alt={dish.name}/>
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                </CardBody>
            </Card>
        );
    } else {
        return( 
            <div></div>
        );
    }
}

const RenderComments = ({comments}) => {
    if(comments != null) {
        const commentsDisplay = comments.map(com => {
            return (
                <li key={com.id}>
                    <p>{com.comment}</p>
                    <p>
                        {com.author}, 
                        {new Intl.DateTimeFormat('en-US', {year: 'numeric', month: 'short', day: '2-digit'})
                        .format(new Date(Date.parse(com.date)))}
                    </p>
                </li>
            )
        })
        return (    
            <React.Fragment>
                <h4 className="m-3">Comments</h4>
                <ul className="list-unstyled">
                    {commentsDisplay}
                </ul>
            </React.Fragment>
        ); 

    }else {
        return( 
            <div></div>
        );
    }    
}

const DishDetail = props => {
    return (
        <div className="container">
            <div className="row">
                <Breadcrumb>
                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
            </div>
            
            <div className="row">
                <div className="col-md-5 m-1 col-12">
                    <RenderDish dish={props.dish}/>
                </div>
                <div className="col-md-5 m-1 col-12">
                    <RenderComments comments={props.comments}/>
                    <CommentForm/>
                </div>
            </div>
        </div>
    )
}

export default DishDetail;
